import React,{Fragment, Component} from 'react'
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth"
import {Link} from 'react-router-dom'
import { FacebookLoginButton, GoogleLoginButton, GithubLoginButton, TwitterLoginButton, InstagramLoginButton } from "react-social-login-buttons";
import { SocialIcon } from 'react-social-icons';
import { slide as Menu } from 'react-burger-menu'
import {loginWithFacebook} from './firebase_config.js'
import logoImg from './adoptarApp_logo.png'
import {Navbar, NavItem, Button, Icon } from 'react-materialize';
import 'materialize-css/dist/css/materialize.min.css';
import ('./Login.css');









class Login extends Component{

    constructor(props){
        super(props)
   this.state = {
    userInfo:{
           name:''
       }
   }
    this.manageLogin = this.manageLogin.bind(this)
    }

    manageLogin = (selection,event) =>{
        console.log("value : ",selection)
        const userData = loginWithFacebook()
        return this.props.loginUpdate(userData)
        /*props.loginUpdate()
        switch(selection) {
            case 'Facebook':
              return handleFacebookAuth()
            default:
              return 'Facebook';
          }

          handleFacebookAuth = ()=>{

            loginWithFacebook();

        }    

        e.preventDefault();
        loginWithFacebook()*/
    };





onClick(){
    alert("CLICKED")
}

handleClick() {
    this.setState({
        open: !this.state.open
    });
}




render(){

    return(

            <Fragment className="main-contanier">

                <Navbar className="navbar" edge="right" centerLogo brand={<a><img src={logoImg}/>AdoptarApp</a>} alignLinks="right">
                <Button node="a">
                    Buscar Mascota
                </Button>
                <Button node="a">
                Donar
                </Button>
                <Button node="a">
                Acerca de AdoptApp
                </Button>


                </Navbar>
                <div className="social">
                    <h6 className="social-title">Por favor, regístrese o ingrese para continuar</h6>
                    <div className="login-container">
                        <p>Seleccione su red social favorita</p>
                        <button className="facebook-btn" onClick={(event) => {this.manageLogin('Facebook', event)}}>Facebook</button>
                        <button className="gootle-btn">Google</button>
                        <button className="twitter-btn">Twitter</button>

                    </div>
                </div>
                {this.props.isAuthed ? <WelcomeMsg userInfo={this.state.userInfo}/>:null}
            </Fragment>


        /*
              <div className="App">
                {this.state.isSignedIn ? (
                  <span>
                    <div>Signed In!</div>
                    <button onClick={() => firebase.auth().signOut()}>Sign out!</button>
                    <h1>Welcome {firebase.auth().currentUser.displayName}</h1>
                    <img
                      alt="profile picture"
                      src={firebase.auth().currentUser.photoURL}
                    />
                  </span>
                ) : (
                  <StyledFirebaseAuth
                    uiConfig={this.uiConfig}
                    firebaseAuth={firebase.auth()}
                  />
                )}
              </div>
            )
          }*/
        /*
        <Fragment className="main-contanier">

            <Navbar className="navbar" edge="right" centerLogo brand={<a><img src={logoImg}/>AdoptarApp</a>} alignLinks="right">
            <Button node="a">
                Buscar Mascota
            </Button>
            <Button node="a">
            Donar
            </Button>
            <Button node="a">
            Acerca de AdoptApp
            </Button>


            </Navbar>
            <div className="social">
                <h6 className="social-title">Por favor, regístrese o ingrese para continuar</h6>
                <div className="login-container">
                    <p>Seleccione su red social favorita</p>
                    <button className="facebook-btn" >Facebook</button>
                    <button className="gootle-btn">Google</button>
                    <button className="twitter-btn">Twitter</button>

                </div>
            </div>
        </Fragment>*/
        )
    }

}

class SignInGoogleBase extends Component {
    constructor(props) {
      super(props);

      this.state = { error: null };
    }

    onSubmit = e =>{
        e.preventDefault();
        loginWithFacebook()
    };



    render() {
      const { error } = this.state;

      return (
        <form onSubmit={this.onSubmit}>
          <button type="submit">Sign In with Google</button>

          {error && <p>{error.message}</p>}
        </form>
      );
    }
  }


const WelcomeMsg = (props)=>{
    return(
        <h6>Hello, {props.userInfo.name}</h6>
    )
}


  export default Login;
